
public class For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=23;
		int j=24;
		for (i=1;i<=10;++i)
		{
			System.out.println("started learning the flow control statements");
		}
		for (j=1;j<=10;++j)
		{
			System.out.println("It helps to learn java");
		}
	}

}
